package net.jeeshop.services.front.email.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.front.email.EmailService;import net.jeeshop.services.front.email.bean.Email;import net.jeeshop.services.front.email.dao.EmailDao;public class EmailServiceImpl extends ServersManager<Email> implements		EmailService {	private EmailDao emailDao;	public void setEmailDao(EmailDao emailDao) {		this.emailDao = emailDao;	}	@Override	public void updateEmailInvalidWhenReg(Email email) {		emailDao.updateEmailInvalidWhenReg(email);	}}