package net.jeeshop.web.action.manage.accountRank;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import net.jeeshop.core.BaseAction;import net.jeeshop.core.ManageContainer;import net.jeeshop.core.exception.NotThisMethod;import net.jeeshop.services.manage.accountRank.AccountRankService;import net.jeeshop.services.manage.accountRank.bean.AccountRank;/** * 会员等级 * @author jqsl2012@163.com * */@Scope("prototype")   @Controller   @RequestMapping("/manage/accountRank")public class AccountRankAction extends BaseAction<AccountRank> {	private static final long serialVersionUID = 1L;	@Autowired	private AccountRankService accountRankService;	{		toList = "/manage/accountRank/accountRankList";		toEdit = "/manage/accountRank/accountRankEdit";		toAdd = "/manage/accountRank/accountRankEdit";	}		@ModelAttribute	public void initStrutsActionParam(){		this.server = accountRankService ;	}		protected void selectListAfter() {		pager.setPagerUrl(getBasePath()+"/manage/accountRank/selectList.action");	}	public AccountRank getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new AccountRank();		}	}	public void insertAfter(AccountRank e) {		e.clear();	}	/**	 * 不支持此方法	 */	@Override	public String insert() throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}	/**	 * 不支持此方法	 */	@Override	public String deletes() throws Exception {		throw new NotThisMethod(ManageContainer.not_this_method);	}}