package net.jeeshop.web.action.manage.email;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.email.EmailService;import net.jeeshop.services.manage.email.bean.Email;@Scope("prototype")   @Controller   @RequestMapping("/manage/email")public class EmailAction extends BaseAction<Email> {	private static final long serialVersionUID = 1L;	@Autowired	private EmailService emailService;	{		toList = "/manage/email/emailList";		toAdd = "/manage/email/emailEdit";		toEdit = "/manage/email/emailEdit";	}		@ModelAttribute	public void initStrutsActionParam(){		this.server = emailService ;	}	protected void selectListAfter() {		pager.setPagerUrl(getBasePath()+"/manage/email/selectList.action");	}	public Email getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Email();		}	}	public void insertAfter(Email e) {		e.clear();	}}