package net.jeeshop.web.action.manage.emailNotifyProduct;import java.io.IOException;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.emailNotifyProduct.EmailNotifyProductService;import net.jeeshop.services.manage.emailNotifyProduct.bean.EmailNotifyProduct;import net.jeeshop.services.manage.emailNotifyProduct.impl.EmailNotifyProductServiceImpl;@Scope("prototype")   @Controller   @RequestMapping("/manage/emailNotifyProduct")public class EmailNotifyProductAction extends BaseAction<EmailNotifyProduct> {	private static final long serialVersionUID = 1L;	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(EmailNotifyProductAction.class);	@Autowired	private EmailNotifyProductService emailNotifyProductService;	{		toList = "/manage/emailNotifyProduct/emailNotifyProductList";		toAdd = "/manage/emailNotifyProduct/emailNotifyProductEdit";		toEdit = "/manage/emailNotifyProduct/emailNotifyProductEdit";	}		@ModelAttribute	public void initStrutsActionParam(){		this.server = emailNotifyProductService ;	}		protected void selectListAfter() {		pager.setPagerUrl(getBasePath()+"/manage/emailNotifyProduct/selectList.action");	}	public EmailNotifyProduct getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new EmailNotifyProduct();		}	}	public void insertAfter(EmailNotifyProduct e) {		e.clear();	}		@RequestMapping("/autoNotify")	public String autoNotify() throws IOException{		logger.error("autoNotify...");		emailNotifyProductService.autoNotify();		super.write("success");		return null;	}}