package net.jeeshop.web.action.manage.express;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.express.ExpressService;import net.jeeshop.services.manage.express.bean.Express;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;@Scope("prototype")   @Controller   @RequestMapping("/manage/express")public class ExpressAction extends BaseAction<Express> {	private static final long serialVersionUID = 1L;		@Autowired	private ExpressService expressService;	{		toList = "/manage/express/expressList";		toAdd = "/manage/express/expressEdit";		toEdit = "/manage/express/expressEdit";	}		protected void selectListAfter() {		pager.setPagerUrl(getBasePath() +"/manage/express/selectList.action");	}	public Express getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Express();		}	}	public void insertAfter(Express e) {		e.clear();	}		@ModelAttribute	public void initStrutsActionParam(){		this.server = expressService;	}}