package net.jeeshop.web.action.manage.navigation;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.navigation.NavigationService;import net.jeeshop.services.manage.navigation.bean.Navigation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;@Scope("prototype")   @Controller   @RequestMapping("/manage/navigation")public class NavigationAction extends BaseAction<Navigation> {	private static final long serialVersionUID = 1L;	@Autowired	private NavigationService navigationService;	{		toList = "/manage/navigation/navigationList";		toEdit = "/manage/navigation/navigationEdit";		toAdd = "/manage/navigation/navigationEdit";	}		@ModelAttribute	public void initStrutsActionParam(){		this.server = navigationService ;	}		protected void selectListAfter() {		pager.setPagerUrl(getBasePath()+"/navigation/selectList.action");	}	public Navigation getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Navigation();		}		super.initPageSelect();	}	public void insertAfter(Navigation e) {		e.clear();	}		@Override	public String insert() throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.insert();	}		@Override	public String update() throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.update();	}}