package net.jeeshop.web.action.manage.systemlog;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.systemlog.SystemlogService;import net.jeeshop.services.manage.systemlog.bean.Systemlog;import org.apache.commons.lang.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;@Scope("prototype")@Controller@RequestMapping("/manage/systemlog")public class SystemlogAction extends BaseAction<Systemlog> {	private static final long serialVersionUID = 1L;		@Autowired	private SystemlogService systemlogService;	private static String systemlogListAndDetail = null;		@ModelAttribute	public void initStrutsActionParam(){		this.server = systemlogService ;	}		{		toList = "/manage/systemlog/systemlogList";		toAdd = "/manage/systemlog/systemlogEdit";		toEdit = "/manage/systemlog/systemlogEdit";		systemlogListAndDetail = "/manage/systemlog/systemlogListAndDetail";		}		protected void selectListAfter() {		pager.setPagerUrl(getBasePath()+"/manage/systemlog/selectList.action");	}	public Systemlog getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Systemlog();		}		super.initPageSelect();	}	public void insertAfter(Systemlog e) {		e.clear();	}		public String systemlogListAndDetail() throws Exception{		String type = request.getParameter("type");		if(StringUtils.isNotBlank(type)){			e.setType(Integer.valueOf(type));		}		selectList();		return systemlogListAndDetail;	}}